import { expect } from "bun:test";
/*
 * @lc app=leetcode id=1513 lang=typescript
 *
 * [1513] Number of Substrings With Only 1s
 */

// @lc code=start
function numSub(s: string): number {
  const memo = new Map<number, number>();
  const calcTotal = (size: number) => {
    if (memo.has(size)) {
      return memo.get(size);
    }
    var t = 0;

    while (size) {
      t += size--;
    }
    memo.set(size, t);
    return t;
  };
  let ans = 0;
  let l1s = 0;

  for (let i = 0, n = s.length; i <= n; i++) {
    const c = s.charAt(i);

    if (c === "1") {
      l1s++;
    } else if (l1s) {
      ans += calcTotal(l1s);
      l1s = 0;
    }
  }
  return ans % (1e9 + 7);
}
// @lc code=end

expect(numSub("0110111")).toBe(9);
expect(numSub("101")).toBe(2);
expect(numSub("111111")).toBe(21);
expect(numSub("10011")).toBe(4);
expect(
  numSub(
    '""'
  )
).toBe(50547496);

